                min: [-90, -180],
                max: [ 90,  180],

                displayMask : "",
                editMask    : "",
                regexp      : zeroOrMoreSpace + nacChars + oneOrMoreSpace + nacChars + zeroOrMoreSpace,
                placeholder : "J0Z8B S8NRP",
