    /***********************************************************************
    ************************************************************************
    6: Load default name,link,email and error-messages
    ************************************************************************
    ***********************************************************************/
    ns.loadKeyPhraseFile('name-address-link.json', subDir            );
    ns.loadPhraseFile   ('request.json',           'error-code-text' );


